  let inputDate = DateTime.fromISO($json.newDate);
  let hour = inputDate.hour;
  let minute = inputDate.minute;
  let second = inputDate.second; // Added for more precision, though 08:30:00 excludes any seconds for 08:30
  let millisecond = inputDate.millisecond; // Added for more precision

  let isAfter8_30_00 = (hour > 8) || (hour === 8 && minute > 30) || (hour === 8 && minute === 30 && second >= 0);
  let isBefore17_00_00 = (hour < 17); // If you meant strictly before 17:00:00, this is correct.

  // Combine conditions: must be AFTER 08:30:00 AND BEFORE 17:00:00
  let isWithinTimeRange = isAfter8_30_00 && isBefore17_00_00;

  return isWithinTimeRange;
}}
